import React from "react";
import QuoteCard from "./QuoteCard";
import MusicCard from "./MusicCard";

function RecommendationsList({ recommendations, type, onShare }) {
  if (!recommendations || recommendations.length === 0) {
    return <p className="text-gray-200 italic">No recommendations yet.</p>;
  }

  return (
    <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
      {recommendations.map((rec, index) => {
        if (rec.title) {
          // Music card
          return <MusicCard key={index} song={rec} />;
        } else if (rec.quote) {
          // Quote card
          return <QuoteCard key={index} quote={rec} onShare={onShare} />;
        }
        return null;
      })}
    </div>
  );
}

export default RecommendationsList;
