import React, { useState } from "react";
import EmotionSelector from "./components/EmotionSelector";
import RecommendationsList from "./components/RecommendationsList";
import { fetchRecommendations } from "./api/fetchRecommendations";

function App() {
  const [emotion, setEmotion] = useState("Happy");
  const [type, setType] = useState("music");
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleGetRecommendations = async () => {
    setLoading(true);
    const data = await fetchRecommendations(emotion, type);
    setRecommendations(data);
    setLoading(false);
  };

  return (
    <div className="w-full max-w-2xl bg-white/20 backdrop-blur-lg p-8 rounded-2xl shadow-2xl">
      <h1 className="text-3xl font-bold mb-6 text-center">ðŸŽ¶ Emotion-Based Recommender</h1>

      <div className="flex flex-col gap-4">
        <EmotionSelector emotion={emotion} setEmotion={setEmotion} type={type} setType={setType} />

        <button
          onClick={handleGetRecommendations}
          className="bg-gradient-to-r from-pink-500 to-yellow-500 text-white font-semibold py-2 px-4 rounded-xl shadow-md hover:scale-105 transition-transform"
        >
          {loading ? "Loading..." : "âœ¨ Get Recommendations"}
        </button>
      </div>

      <div className="mt-6">
        <RecommendationsList recommendations={recommendations} type={type} />
      </div>
    </div>
  );
}

export default App;
