import React, { useState } from 'react';
import EmotionSelector from './components/EmotionSelector';
import RecommendationsList from './components/RecommendationsList';
import { fetchRecommendations } from './api/fetchRecommendations';

function App() {
    const [emotion, setEmotion] = useState('');
    const [type, setType] = useState('music');
    const [recommendations, setRecommendations] = useState([]);

    const getRecommendations = async () => {
        if (!emotion) return alert('Select an emotion!');
        const data = await fetchRecommendations(emotion, type);
        setRecommendations(data);
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Emotion-Based Recommender</h1>
            <EmotionSelector emotion={emotion} setEmotion={setEmotion} />
            <div>
                <label>Type: </label>
                <select value={type} onChange={e => setType(e.target.value)}>
                    <option value="music">Music</option>
                    <option value="quotes">Quotes</option>
                </select>
            </div>
            <button onClick={getRecommendations}>Get Recommendations</button>
            <RecommendationsList recommendations={recommendations} type={type} />
        </div>
    );
}

export default App;
