import React from "react";

function RecommendationsList({ recommendations, type }) {
  if (!recommendations || recommendations.length === 0) {
    return <p className="text-gray-200 italic">No recommendations yet.</p>;
  }

  return (
    <div className="space-y-4">
      {recommendations.map((rec, index) => (
        <div
          key={index}
          className="p-4 bg-white/30 rounded-xl shadow-md hover:scale-[1.02] transition-transform"
        >
          {type === "music" ? (
            <div>
              {rec.albumImage && (
                <img
                  src={rec.albumImage}
                  alt={rec.title}
                  className="w-20 h-20 rounded-lg mb-2"
                />
              )}
              <p className="font-semibold">{rec.title}</p>
              <p className="text-sm text-gray-200">by {rec.artist}</p>
              {rec.url && (
                <a
                  href={rec.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-pink-300 underline text-sm"
                >
                  Listen on Spotify
                </a>
              )}
            </div>
          ) : (
            <div>
              <p className="italic text-lg">“{rec.quote}”</p>
              <p className="text-sm text-gray-200 mt-1">— {rec.author}</p>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default RecommendationsList;
