import React from "react";
import { Music2, ExternalLink } from "lucide-react";
import PropTypes from "prop-types";

function MusicCard({ music }) {
  return (
    <div className="bg-white/90 backdrop-blur-sm rounded-xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 border border-white/50 flex flex-col items-center text-center">
      {/* Album art or fallback icon */}
      {music.albumImage ? (
        <img
          src={music.albumImage}
          alt={music.title}
          className="w-24 h-24 rounded-lg mb-4 shadow-md"
        />
      ) : (
        <div className="w-24 h-24 flex items-center justify-center bg-gradient-to-r from-green-400 to-blue-500 text-white rounded-lg mb-4 shadow-md">
          <Music2 size={32} />
        </div>
      )}

      {/* Song details */}
      <h3 className="font-semibold text-lg text-gray-800">{music.title}</h3>
      <p className="text-sm text-gray-600 mb-2">by {music.artist}</p>

      {/* Spotify link */}
      {music.url && (
        <a
          href={music.url}
          target="_blank"
          rel="noopener noreferrer"
          className="inline-flex items-center gap-1 text-purple-600 hover:text-purple-800 text-sm font-medium mt-2"
        >
          Listen on Spotify <ExternalLink size={14} />
        </a>
      )}
    </div>
  );
}

MusicCard.propTypes = {
  music: PropTypes.shape({
    title: PropTypes.string.isRequired,
    artist: PropTypes.string.isRequired,
    albumImage: PropTypes.string,
    url: PropTypes.string,
  }).isRequired,
};

export default MusicCard;
